import React, { useState } from 'react';
import { Carousel } from 'react-bootstrap';

const HomePage = () => {

    const [filmes, setFilmes] = useState([
        {
          title: 'Filme 1',
          description: 'Descrição do Filme 1',
          imageUrl: 'url_da_imagem_1.jpg'
        },
        {
          title: 'Filme 2',
          description: 'Descrição do Filme 2',
          imageUrl: 'url_da_imagem_2.jpg'
        },
        // Adicione mais filmes conforme necessário
      ]);

      //realizar ações ao iniciar componente
    useEffect(() => {
        async function buscarDados(){

            const response = axios.get("http://143.198.156.185/api/home").then(function (value) {
                console.log(value);
              }) .catch(function (value) {
                console.log(value);
              });

        }

        buscarDados();
    }, []);
    
  return (
    <Carousel>
      {filmes.map((filme, index) => (
        <Carousel.Item key={index}>
          <img
            className="d-block w-100"
            src={filme.imageUrl}
            alt={filme.title}
          />
          <Carousel.Caption>
            <h3>{filme.title}</h3>
            <p>{filme.description}</p>
          </Carousel.Caption>
        </Carousel.Item>
      ))}
    </Carousel>
  );
};

export default HomePage;
