import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { Carousel, Container, Row, Col, Card } from 'react-bootstrap';

const HomePage = () => {

    const [filmes, setFilmes] = useState([]);

    //realizar ações ao iniciar componente
    useEffect(() => {
        async function buscarDados() {

            const response = axios.get("http://143.198.156.185/api/home").then(function (value) {
                setFilmes(value.data);
            }).catch(function (value) {
                console.log(value);
            });

        }

        buscarDados();
    }, []);

    return (
        <>
            <Row className="pt-2 pb-3">
                <Carousel style={{ maxHeight: "500px" }}>
                    {filmes.map((filme, index) => (
                        <Carousel.Item key={index}>
                            <img
                                className="d-block w-100"
                                src={filme.imagens[2]}
                                alt={filme.titulo}
                            />
                            <Carousel.Caption>
                                <h3>{filme.title}</h3>
                                <p>{filme.sinopse}</p>
                            </Carousel.Caption>
                        </Carousel.Item>
                    ))}
                </Carousel>
            </Row>
            <Row className="pt-2 pb-3">
                {filmes.map((filme, index) => (
                    <Col xs={6} sm={4} md={3} lg={2}>
                        <Card>
                            <Card.Img variant="top" src={filme.url_thumbnail} />
                            <Card.Body>
                                <Card.Title>{filme.titulo}</Card.Title>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
        </>

    );
};

export default HomePage;
