import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Image } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // React Router

const apiKey = 'YOUR_API_KEY'; // Substitua por sua chave API

function MovieList() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`https://api.themoviedb.org/3/movie/top_rated?api_key=${apiKey}`)
      .then((response) => response.json())
      .then((data) => setMovies(data.results));
  }, []);

  return (
    <Container>
      <h2>Melhores Filmes</h2>
      <Row>
        {movies.map((movie) => (
          <Col md={4} className="mb-3" key={movie.id}>
            <Card>
              <Link to={`/movie/${movie.id}`}>
                <Image variant="top" src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt={movie.title} />
              </Link>
              <Card.Body>
                <Card.Title>{movie.title}</Card.Title>
                <Card.Text>
                  <span>Avaliação: </span>{movie.vote_average}/10
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default LivroPage;
