import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Form, Button, Alert, Container } from 'react-bootstrap';
import { useParams } from 'react-router-dom';

const FilmesPage = () => {
    const { id } = useParams();

    const [filme, setFilme] = useState('');

    useEffect(() => {
        async function buscarFilme() {
            const filmes = axios.get(`http://143.198.156.185/api/filmes`).then(function (value) {
                setFilme(value.data);
            }).catch(function (value) {
                console.log(value);
            });
        }

        buscarFilme();
    });

    return (
        <div className="container mt-5">
            {filme.map((film)=>(
                <div className='card text-bg-dark'>
                {film.imagens && film.imagens[0] && (
                    <img src={film.imagens[0].url} class="card-img" alt="..."></img>)}
                    <div class="card-mg-overlay">
                        <h6 class="card-title text-center fs-2">{film.titulo}</h6>
                    </div>
            </div>  
            ))}
        </div>
    );
};

export default FilmesPage;
