import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';
import { useParams } from 'react-router-dom';

const LivrosPage = () => {
    const { id } = useParams();

    const [livros, setLivros] = useState('');

    useEffect(() => {
        async function buscarLivro() {
            const livros = axios.get(`http://143.198.156.185/api/livros`).then(function (value) {
                setLivros(value.data);
            }).catch(function (value) {
                console.log(value);
            });
        }

        buscarLivro();
    });

    return (
        <div className="container mt-5">
          {(livros || []).map((livro) => (
            <div className="card text-bg-dark">
              {livro.imagens && livro.imagens[0] && (
                <img src={livro.imagens[0].url} class="card-img" alt="..."></img>
              )}
              <div class="card-mg-overlay">
                <h6 class="card-title text-center fs-2">{livro.titulo}</h6>
              </div>
            </div>
          ))}
        </div>
      );
};

export default LivrosPage;
