import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

const LoginPage = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);

    async function handleSubmit(e){
        e.preventDefault();

        let reponse = axios.post("")

        const response = await fetch('http://143.198.156.185/api/auth/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, password }),
        });

        const data = await response.json();

        if (response.ok) {
            //preparar para fazer o redirecionamento para tela do usuário
            // vocês ainda não aprenderam isso, fica sussa
            console.log('Logado com sucesso');
            setSuccess(data.message || 'Logado com sucesso');
            setError(null);
        } else {
            setError(data.message || 'Erro ao realizar login');
            setSuccess(null);
        }

    };

    return (
        <div className="card">

            <div className="card-header">
                Login
            </div>
            <div className="card-body">
                {error && <Alert variant="danger">{error}</Alert>}
                {success && <Alert variant="success">{success}</Alert>}
                <Form onSubmit={handleSubmit}>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Email address</Form.Label>
                        <Form.Control
                            type="email"
                            placeholder="Enter email"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </Form.Group>

                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password</Form.Label>
                        <Form.Control
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </Form.Group>

                    <Button variant="primary" type="submit" className="mt-2">
                        Entrar
                    </Button>
                </Form>
            </div>

        </div>
    );
};

export default LoginPage;
