import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Image } from 'react-bootstrap';
import { Link } from 'react-router-dom'; // React Router

const apiKey = 'http://143.198.156.185/api/livros/porid/1';
function LivroPage() {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch(`http://143.198.156.185/api/livros/porid/1`)
      .then((response) => response.json())
      .then((data) => setMovies(data.results));
  }, []);

  return (
    <Container>
      <h2>Melhores livros</h2>
      <Row>
        {livros.map((livros) => (
          <Col md={4} className="mb-3" key={livros.id}>
            <Card>
              <Link to={`/livros/${livros.id}`}>
                <Image variant="top" src={`https://image.tmdb.org/t/p/w500/${livros.poster_path}`} alt={livros.title} />
              </Link>
              <Card.Body>
                <Card.Title>{livros.title}</Card.Title>
                <Card.Text>
                  <span>Avaliação: </span>{livros.qnt}/10
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </Container>
  );
}

export default LivroPage;
